package javaprac;

public class DefaultVariableValues {
	static byte bytevariables;
	static short shortvariable;
	static int intvariable;
	static float floatvariable;
	static boolean booleanvariable;
	static char charvariable;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// If the variable is declared inside a method
	
		System.out.println("Byte Variable: " + bytevariables);
		
		System.out.println("Short Variable: " + shortvariable);
		
		System.out.println("Integer Variable: " + intvariable);
		
		System.out.println("Float Variable: " + floatvariable);
		
        System.out.println("Boolean Variable: " + booleanvariable);
       
        System.out.println("Character Variable: " +charvariable);

	}

}
