package javaprac;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//primitive data types
		/*
		 * Byte - min: -128 Max: 127
		 * Default:0
		 */
		byte bytevariables = 100;
		System.out.println("Byte Variable: " + bytevariables);
		
		/*
		 * Short - min: -32,768 max: 32767
		 * Default :0
		 *
		 */
		short shortvariable = 10000;
		System.out.println("Short Variable: " + shortvariable);
		
		
		/*
		 * Int - min: -2147483648 , max : 2147483647, default :0
		 * 
		 */
		int intvariable = 75755;
		System.out.println("Integer Variable: " + intvariable);
		
		/*
		 * Long - min: -2^63 max:2^63
		 */
		
		/*
		 * float ; Default : 0.0f
		 */
		float floatvariable = 20.8f;
		System.out.println("Float Variable: " + floatvariable);
		
		
		/*
		 * double ; default 0.0d
		 */
		
		
		/*
		 * boolean - true and false
		 * default : false
		 */
       boolean booleanvariable = true;
       System.out.println("Boolean Variable: " + booleanvariable);
       
       
       /*
        * char : min: 0 , max : 65535
        */
       
       char charvariable = 65535;
       System.out.println("Character Variable: " +charvariable);
	}

}
